import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; // Leaflet 추가
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js'; // 한글 매핑 추가
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D`
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!dustData || dustData.length === 0) {
    return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;
  }

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  // PM10 수치에 따라 색상을 반환하는 함수
  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 15) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 35) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 75) return 'rgba(255, 245, 89, 0.7)';
    if (pmValue <= 150) return 'rgba(255, 152, 0, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  // PM10 수치에 따라 사각형 마커의 스타일과 지역명을 반환하는 함수
  const getMarkerStyleByPM10 = (value) => {
    if (!value || value === '-') return {
      width: '40px',
      height: '40px',
      backgroundColor: 'rgba(128, 128, 128, 0.7)',
    };
    const pmValue = parseFloat(value);
    const size = pmValue <= 15 ? 25 :
                 pmValue <= 35 ? 30 :
                 pmValue <= 75 ? 35 :
                 pmValue <= 150 ? 40 : 50;

    return {
      width: `${size}px`,
      height: `${size}px`,
      backgroundColor: getColorByPM10(value),
      borderRadius: '5px', // 사각형 모양
      border: '2px solid #333',
      fontSize: '14px', // 한글 지역명에 맞게 폰트 크기 조정
      fontWeight: 'bold',
    };
  };

  return (
    <div className="dust-container">
      <main className="dust-content">
        <p>오늘의 미세먼지 지도</p>

        <MapContainer
          center={[36.5, 128.5]}
          zoom={7}
          dragging={false}
          scrollWheelZoom={false}
          zoomControl={false}
          className="map-container"
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            maxZoom={19}
          />

          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[cityMapping[city]];
            if (!coordinates) return null;

            const markerStyle = getMarkerStyleByPM10(value);
            const icon = L.divIcon({
              className: 'custom-icon',
              html: ` 
                <div class="marker-box" 
                     style="width:${markerStyle.width}; height:${markerStyle.height}; background-color:${markerStyle.backgroundColor}; border-radius:${markerStyle.borderRadius}; border:${markerStyle.border}; display: flex; align-items: center; justify-content: center;">
                  <span class="marker-label" 
                        style="color: rgb(0, 0, 0, 0.9); font-size: ${markerStyle.fontSize}; font-weight: ${markerStyle.fontWeight};">
                    ${cityMapping[city] || city}
                  </span>
                </div>`,
              iconSize: [parseInt(markerStyle.width), parseInt(markerStyle.height)] // 아이콘 크기 설정
            });

            return (
              <Marker
                key={city}
                position={coordinates}
                icon={icon}
              >
                <Popup>
                  <div style={{ textAlign: 'center', fontFamily: 'Roboto, sans-serif', color: '#333' }}>
                    <strong style={{ fontSize: '16px', color: '#007bff' }}>{cityMapping[city]}</strong> <br />
                    미세먼지(PM10): <span style={{ fontWeight: 'bold' }}>{value}㎍/m³</span> <br />
                    상태: 
                    <span style={{ fontWeight: 'bold', color: getColorByPM10(value) }}>
                      {parseFloat(value) <= 30 ? '좋음' :
                       parseFloat(value) <= 80 ? '보통' :
                       parseFloat(value) <= 150 ? '나쁨' : '매우 나쁨'}
                    </span>
                  </div>
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>

        <p>측정일시: {dustData.dataTime}</p>

        <div className="button-container">
          <Link to="/analyze">
            <button className="AI-button">AI 분석 페이지 이동</button>
          </Link>
          <Link to="/main">
            <button className="AI-button">메인화면</button>
          </Link>
        </div>
      </main>
    </div>
  );
};

export default TodayDust;
