import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState(null);
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = 'your-api-key-here';

  // Fetch dust data
  const fetchDustData = async () => {
    try {
      const response = await fetch(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=${API_KEY}`
      );
      const data = await response.json();
      return data.response.body.items[0];
    } catch (error) {
      console.error('Error fetching dust data:', error);
      throw error;
    }
  };

  // Fetch DB data
  const fetchDbData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/products/');
      return response.data;
    } catch (error) {
      console.error('Error fetching DB data:', error);
      throw error;
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        const dustResult = await fetchDustData();
        setDustData(dustResult);

        const dbResult = await fetchDbData();
        setDbData(dbResult);

        if (dbResult.length > 0) {
          const firstRegion = dbResult[0].region;
          const firstRegionDetail = dbResult[0].region_detail;

          const detailData = await fetchDetailData(firstRegion, firstRegionDetail);
          setApiData([{ 
            region: firstRegion, 
            regionDetail: firstRegionDetail,
            apiData: detailData 
          }]);
        }
      } catch (error) {
        setError(error.message);
        console.error('fetchAllData 에러:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  // Extract data for the circular gauges (PM10, PM2.5, CO2, O3)
  const extractAirQualityData = (data) => {
    if (!data) return { pm10: 0, pm25: 0, co2: 0, o3: 0 };

    const { pm10Value, pm25Value, coValue, o3Value } = data;
    return {
      pm10: parseFloat(pm10Value) || 0,
      pm25: parseFloat(pm25Value) || 0,
      co2: parseFloat(coValue) || 0,
      o3: parseFloat(o3Value) || 0
    };
  };

  // Function to convert data to a percentage for the progress bar
  const convertToPercentage = (value, maxValue) => Math.min((value / maxValue) * 100, 100);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if (!dustData) {
    return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;
  }

  const { pm10, pm25, co2, o3 } = extractAirQualityData(dustData);

  return (
    <div className="dust-container">
      <h2>실시간 대기질 정보</h2>
      <div className="gauge-container">
        <div className="gauge">
          <CircularProgressbar 
            value={convertToPercentage(pm10, 150)} 
            text={`${pm10} µg/m³`} 
            styles={buildStyles({ pathColor: 'rgba(244, 67, 54, 0.7)', textColor: '#333' })} 
          />
          <p>PM10</p>
        </div>
        <div className="gauge">
          <CircularProgressbar 
            value={convertToPercentage(pm25, 35)} 
            text={`${pm25} µg/m³`} 
            styles={buildStyles({ pathColor: 'rgba(76, 175, 80, 0.7)', textColor: '#333' })} 
          />
          <p>PM2.5</p>
        </div>
        <div className="gauge">
          <CircularProgressbar 
            value={convertToPercentage(co2, 400)} 
            text={`${co2} µg/m³`} 
            styles={buildStyles({ pathColor: 'rgba(255, 152, 0, 0.7)', textColor: '#333' })} 
          />
          <p>CO2</p>
        </div>
        <div className="gauge">
          <CircularProgressbar 
            value={convertToPercentage(o3, 180)} 
            text={`${o3} µg/m³`} 
            styles={buildStyles({ pathColor: 'rgba(76, 80, 175, 0.7)', textColor: '#333' })} 
          />
          <p>O3</p>
        </div>
      </div>

      <div className="api-data-section">
        <ul className="api-data-list">
          {apiData.map((data) => (
            <li key={data.region} className="api-data-item">
              <h4>{data.region} - {data.regionDetail} 지역 정보</h4>
              {data.apiData.length === 0 ? (
                <p>해당 지역의 데이터가 없습니다.</p>
              ) : (
                data.apiData.map((item, index) => (
                  <div key={index} className="measurement-info">
                    <p>측정 시간: {item.dataTime}</p>
                    <p>PM10: {item.pm10Value}㎍/m³</p>
                    <p>PM2.5: {item.pm25Value}㎍/m³</p>
                    <p>CO2: {item.coValue}㎍/m³</p>
                    <p>O3: {item.o3Value}㎍/m³</p>
                  </div>
                ))
              )}
            </li>
          ))}
        </ul>
      </div>

      <MapContainer
        center={[36.5, 128.5]}
        zoom={7}
        scrollWheelZoom={false}
        zoomControl={false}
        className="map-container"
        doubleClickZoom={false}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
          maxZoom={19}
        />
      </MapContainer>

      <div className="button-container">
        <Link to="/analyze">
          <button className="AI-button">AI 분석 페이지 이동</button>
        </Link>
        <Link to="/main">
          <button className="AI-button">메인화면</button>
        </Link>
      </div>
    </div>
  );
};

export default TodayDust;
