import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, CircleMarker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=YOUR_SERVICE_KEY'
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  return (
    <div className="today-container">
      <header className="today-header">
        <h1>TodayDust Page</h1>
      </header>

      <main className="today-content">
        <p>오늘의 미세먼지 지도</p>

        <div className="map-container">
          <MapContainer
            center={[36.5, 127.5]}
            zoom={7}
            scrollWheelZoom={true}
            style={{ width: '100%', height: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            {cities.map(([city, value]) => {
              const coordinates = cityMapping[city];
              if (!coordinates) return null;

              return (
                <CircleMarker
                  key={city}
                  center={coordinates}
                  radius={8}
                  color="blue"
                  fillOpacity={0.7}
                >
                  <Popup>
                    <strong>{city}</strong> <br />
                    미세먼지(PM10): {value}㎍/m³
                  </Popup>
                </CircleMarker>
              );
            })}
          </MapContainer>
        </div>

        <p>측정일시: {dustData.dataTime}</p>

        <Link to="/main">
          <button className="main-button">Main</button>
        </Link>
      </main>
    </div>
  );
};

export default TodayDust;
