import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

// 네비게이션 바 컴포넌트
const Navbar = () => {
  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/todayDust" className="navbar-logo">
          DustAI
        </Link>

        <div className="navbar-menu">
          <Link to="/analyze" className="navbar-link">
            AI 분석 페이지 이동
          </Link>
          <Link to="/dustdata" className="navbar-link">
            미세먼지 데이터
          </Link>
          <Link to="/login" className="navbar-login">
            로그인
          </Link>
        </div>
      </div>
    </nav>
  );
};

// API 키 설정
const API_KEY = 'pVHLUl3UGVDmm%2BB0aUxMttCQnoCePxtWLb4WJKushUW6XjTX93l89IkU%2FdELSR6nZ318fR3efuSBb3gVpjAFFA%3D%3D';

const TodayDust = () => {
  // 상태 관리
  const [dustData, setDustData] = useState(null);
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [selectedData, setSelectedData] = useState(null); // 선택된 시간의 데이터
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 상세 데이터 가져오기
  const fetchDetailData = async (region, regionDetail) => {
    try {
      const response = await axios.get(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureSidoLIst?sidoName=${region}&searchCondition=HOUR&pageNo=1&numOfRows=1000&returnType=json&serviceKey=${API_KEY}`
      );

      const allItems = response.data.response.body.items;
      return allItems.filter(item => item.cityName === regionDetail);
    } catch (error) {
      console.error(`${region} 상세 데이터 가져오기 실패:`, error);
      return [];
    }
  };

  // 데이터 초기 로드
  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);

        // DB에서 지역 정보 가져오기
        const dbResult = await axios.get('http://127.0.0.1:8000/api/products/');
        setDbData(dbResult.data);

        if (dbResult.data.length > 0) {
          const { region, region_detail: regionDetail } = dbResult.data[0];
          const detailData = await fetchDetailData(region, regionDetail);
          setApiData(detailData);

          // 기본 선택 데이터로 첫 번째 값 설정
          if (detailData.length > 0) {
            setSelectedData(detailData[0]);
          }
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  // 값에 따른 색상 가져오기
  const getColorByValue = (value, thresholds, colors) => {
    if (!value || value === '-') return colors[0];
    const numericValue = parseFloat(value);
    for (let i = 0; i < thresholds.length; i++) {
      if (numericValue <= thresholds[i]) return colors[i + 1];
    }
    return colors[colors.length - 1];
  };

  // 로딩 및 에러 상태 처리
  if (loading) return <div className="loading">로딩중...</div>;
  if (error) return <div className="error">에러: {error}</div>;
  if (!dustData || apiData.length === 0) return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;

  return (
    <>
      <Navbar />
      <div className="dust-container" style={{ marginTop: '64px' }}>

        {/* 시간 선택 Dropdown */}
        <div className="dropdown-container">
          <h3>시간별 미세먼지 데이터</h3>
          <select
            onChange={(e) => {
              const selectedTime = e.target.value;
              const data = apiData.find(d => d.dataTime === selectedTime);
              setSelectedData(data);
            }}
          >
            {apiData.map((item, index) => (
              <option key={index} value={item.dataTime}>
                {item.dataTime} {/* 시간 표시 */}
              </option>
            ))}
          </select>
        </div>

        {/* CircularProgressbar */}
        <div style={{ width: '200px', margin: '20px auto' }}>
          {selectedData ? (
            <CircularProgressbar
              value={parseFloat(selectedData.pm10Value) || 0}
              maxValue={150}
              text={selectedData.pm10Value ? `${selectedData.pm10Value}㎍/m³` : 'N/A'}
              styles={buildStyles({
                pathColor: (() => {
                  const value = parseFloat(selectedData.pm10Value);
                  if (value <= 30) return 'rgba(76, 80, 175, 0.7)'; // 좋음
                  if (value <= 80) return 'rgba(76, 175, 80, 0.7)'; // 보통
                  if (value <= 150) return 'rgba(255, 245, 89, 0.7)'; // 나쁨
                  return 'rgba(244, 67, 54, 0.7)'; // 매우 나쁨
                })(),
                textColor: '#333',
              })}
            />
          ) : (
            <p>데이터를 선택해주세요.</p>
          )}
        </div>

        {/* 범례 */}
        <ul>
          <li>
            <span style={{ backgroundColor: 'rgba(76, 80, 175, 0.7)' }}></span>(0~30) : 좋음
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(76, 175, 80, 0.7)' }}></span>(~80) : 보통
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(255, 245, 89, 0.7)' }}></span>(~150) : 나쁨
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(244, 67, 54, 0.7)' }}></span>(150+) : 매우 나쁨
          </li>
        </ul>
      </div>
      <footer className="footer">
        <div className="footer-container">
          <p>&copy; 2024 DustAI. All rights reserved.</p>
          <p>Privacy Policy | Terms of Service</p>
        </div>
      </footer>
    </>
  );
};

export default TodayDust;
