import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState(null);
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = 'zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D';

  // 전국 미세먼지 데이터 조회
  const fetchDustData = async () => {
    try {
      const response = await fetch(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=${API_KEY}`
      );
      const data = await response.json();
      return data.response.body.items[0];
    } catch (error) {
      console.error('Error fetching dust data:', error);
      throw error;
    }
  };

  // DB 데이터 조회
  const fetchDbData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/products/');
      return response.data;
    } catch (error) {
      console.error('Error fetching DB data:', error);
      throw error;
    }
  };

  // 시도별 상세 데이터 조회 - cityName 기준으로 필터링
  const fetchDetailData = async (region, regionDetail) => {
    try {
      const response = await axios.get(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureSidoLIst?sidoName=${region}&searchCondition=DAILY&pageNo=1&numOfRows=100&returnType=json&serviceKey=${API_KEY}`
      );

      const allItems = response.data.response.body.items;

      // 1. cityName이 region_detail과 일치하는 데이터만 먼저 필터링
      const matchingItems = allItems.filter(item => item.cityName === regionDetail);

      // 2. dataTime을 기준으로 정렬 (최신 순)
      matchingItems.sort((a, b) => {
        const dateA = new Date(a.dataTime);
        const dateB = new Date(b.dataTime);
        return dateB - dateA;
      });

      // 3. 가장 최신 데이터만 반환
      return matchingItems.length > 0 ? [matchingItems[0]] : [];

    } catch (error) {
      console.error(`Error fetching detail data for ${region}:`, error);
      return [];
    }
};

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        // 1. 기본 미세먼지 데이터 조회
        const dustResult = await fetchDustData();
        setDustData(dustResult);

        // 2. DB 데이터 조회
        const dbResult = await fetchDbData();
        setDbData(dbResult);

        // 3. DB 데이터의 region과 region_detail을 기반으로 상세 데이터 조회
        if (dbResult.length > 0) {
          const firstRegion = dbResult[0].region;
          const firstRegionDetail = dbResult[0].region_detail;

          const detailData = await fetchDetailData(firstRegion, firstRegionDetail);
          setApiData([{ 
            region: firstRegion, 
            regionDetail: firstRegionDetail,
            apiData: detailData 
          }]);
        }
      } catch (error) {
        setError(error.message);
        console.error('fetchAllData 에러:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  // PM10 수치에 따라 색상을 반환하는 함수
  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 15) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 35) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 75) return 'rgba(255, 245, 89, 0.7)';
    if (pmValue <= 150) return 'rgba(255, 152, 0, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  // PM10 수치에 따라 사각형 마커의 스타일과 지역명을 반환하는 함수
  const getMarkerStyleByPM10 = (value) => {
    if (!value || value === '-') return {
      width: '40px',
      height: '40px',
      backgroundColor: 'rgba(128, 128, 128, 0.7)',
    };

    const pmValue = parseFloat(value);

    // PM10 수치에 따라 크기를 단계별로 설정
    let size = 30; // 기본값
    if (pmValue > 15 && pmValue <= 35) size = 35;
    else if (pmValue > 35 && pmValue <= 75) size = 40;
    else if (pmValue > 75 && pmValue <= 150) size = 45;
    else if (pmValue > 150) size = 50;

    return {
      width: `${size}px`,
      height: `${size}px`,
      backgroundColor: getColorByPM10(value),
      borderRadius: '5px', // 사각형 모양
      border: '2px solid #333',
      fontSize: '14px', // 한글 지역명에 맞게 폰트 크기 조정
      fontWeight: 'bold',
    };
  };
  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if (!dustData) {
    return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;
  }
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  return (
    <div className="dust-container">
      <h2>실시간 대기질 정보</h2>
      <div className="api-data-section">
        <ul className="api-data-list">
          {apiData.map((data) => (
            <li key={data.region} className="api-data-item">
              <h4>{data.region} - {data.regionDetail} 지역 정보</h4>
              {data.apiData.length === 0 ? (
                <p>해당 지역의 데이터가 없습니다.</p>
              ) : (
                data.apiData.map((item, index) => (
                  <div key={index} className="measurement-info">
                    {/* <p>지역: {item.cityName}</p>
                    <p>측정소: {item.stationName}</p> */}
                    <p>측정 시간: {item.dataTime}</p>
                    <p>PM10: {item.pm10Value}㎍/m³</p>
                    <p>PM2.5: {item.pm25Value}㎍/m³</p>
                    <p>CO2: {item.coValue}</p>
                    <p>CO2: {item.coValue}</p>
                  </div>
                ))
              )}
            </li>
          ))}
        </ul>
      </div>
      <main className="dust-content">
        <p>오늘의 미세먼지 지도</p>

        <MapContainer
          center={[36.5, 128.5]}
          zoom={7}
          scrollWheelZoom={false}
          //dragging={false}
          zoomControl={false}
          className="map-container"
          doubleClickZoom={false} // 더블 클릭 확대 비활성화
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            maxZoom={19}
          />

          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[cityMapping[city]];
            if (!coordinates) return null;

            const markerStyle = getMarkerStyleByPM10(value);
            const icon = L.divIcon({
              className: 'custom-icon',
              html: ` 
                <div class="marker-box" 
                     style="width:${markerStyle.width}; height:${markerStyle.height}; background-color:${markerStyle.backgroundColor}; border-radius:${markerStyle.borderRadius}; border:${markerStyle.border}; display: flex; align-items: center; justify-content: center;">
                  <span class="marker-label" 
                        style="color: rgb(0, 0, 0, 0.9); font-size: ${markerStyle.fontSize}; font-weight: ${markerStyle.fontWeight};">
                    ${cityMapping[city] || city}
                  </span>
                </div>`,
              iconSize: [parseInt(markerStyle.width), parseInt(markerStyle.height)] // 아이콘 크기 설정
            });

            return (
              <Marker
                key={city}
                position={coordinates}
                icon={icon}
              >
                <Popup>
                  <div style={{ textAlign: 'center', fontFamily: 'Roboto, sans-serif', color: '#333' }}>
                    <strong style={{ fontSize: '16px', color: '#007bff' }}>{cityMapping[city]}</strong> <br />
                    미세먼지(PM10): <span style={{ fontWeight: 'bold' }}>{value}㎍/m³</span> <br />
                    상태: 
                    <span style={{ fontWeight: 'bold', color: getColorByPM10(value) }}>
                      {parseFloat(value) <= 30 ? '좋음' :
                       parseFloat(value) <= 80 ? '보통' :
                       parseFloat(value) <= 150 ? '나쁨' : '매우 나쁨'}
                    </span>
                  </div>
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>
        <div className="dust-legend">
        <h4>미세먼지 수치 기준 (PM10)</h4>
        <ul>
          <li>
            <span style={{ backgroundColor: 'rgba(76, 80, 175, 0.7)' }}></span> 0~15: 매우 좋음
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(76, 175, 80, 0.7)' }}></span> ~35: 좋음
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(255, 245, 89, 0.7)' }}></span> ~75: 보통
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(255, 152, 0, 0.7)' }}></span> ~150: 나쁨
          </li>
          <li>
            <span style={{ backgroundColor: 'rgba(244, 67, 54, 0.7)' }}></span> 150+: 매우 나쁨
          </li>
        </ul>
      </div>


        <p>측정일시: {dustData.dataTime}</p>

        <div className="button-container">
          <Link to="/analyze">
            <button className="AI-button">AI 분석 페이지 이동</button>
          </Link>
          <Link to="/main">
            <button className="AI-button">메인화면</button>
          </Link>
        </div>
      </main>
    </div>
  );
};

export default TodayDust;
