import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import { ArrowRight, Map, AlertTriangle, Wind } from 'lucude-react';
import 'react-circular-progressbar/dist/styles.css';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState(null);
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = 'pVHLUl3UGVDmm%2BB0aUxMttCQnoCePxtWLb4WJKushUW6XjTX93l89IkU%2FdELSR6nZ318fR3efuSBb3gVpjAFFA%3D%3D';

  // 전국 미세먼지 데이터 조회
  const fetchDustData = async () => {
    try {
      const response = await fetch(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=${API_KEY}`
      );
      const data = await response.json();
      return data.response.body.items[0];
    } catch (error) {
      console.error('Error fetching dust data:', error);
      throw error;
    }
  };

  // DB 데이터 조회
  const fetchDbData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/products/');
      return response.data;
    } catch (error) {
      console.error('Error fetching DB data:', error);
      throw error;
    }
  };

  // 시도별 상세 데이터 조회
  const fetchDetailData = async (region, regionDetail) => {
    try {
      const response = await axios.get(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureSidoLIst?sidoName=${region}&searchCondition=DAILY&pageNo=1&numOfRows=100&returnType=json&serviceKey=${API_KEY}`
      );

      const allItems = response.data.response.body.items;
      const matchingItems = allItems.filter(item => item.cityName === regionDetail);

      matchingItems.sort((a, b) => {
        const dateA = new Date(a.dataTime);
        const dateB = new Date(b.dataTime);
        return dateB - dateA;
      });

      return matchingItems.length > 0 ? [matchingItems[0]] : [];
    } catch (error) {
      console.error(`Error fetching detail data for ${region}:`, error);
      return [];
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        const dustResult = await fetchDustData();
        setDustData(dustResult);

        const dbResult = await fetchDbData();
        setDbData(dbResult);

        if (dbResult.length > 0) {
          const firstRegion = dbResult[0].region;
          const firstRegionDetail = dbResult[0].region_detail;

          const detailData = await fetchDetailData(firstRegion, firstRegionDetail);
          setApiData([{ 
            region: firstRegion, 
            regionDetail: firstRegionDetail,
            apiData: detailData 
          }]);

          // DB 데이터에서 disease 정보 추가
          const diseaseInfo = dbResult.find(item => item.region === firstRegion);
          if (diseaseInfo) {
            setDustData(prevData => ({
              ...prevData,
              disease: diseaseInfo.disease,
              diseaseAdvice: diseaseInfo.diseaseAdvice,
            }));
          }
        }
      } catch (error) {
        setError(error.message);
        console.error('fetchAllData 에러:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 30) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 80) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 150) return 'rgba(255, 245, 89, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  const getColorByPM25 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 15) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 50) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 100) return 'rgba(255, 245, 89, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  const getColorByO3 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 0.1) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 0.3) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 0.5) return 'rgba(255, 245, 89, 0.7)';

    return 'rgba(244, 67, 54, 0.7)';
  };

  const getMarkerStyleByPM10 = (value) => {
    if (!value || value === '-') return {
      width: '40px',
      height: '40px',
      backgroundColor: 'rgba(128, 128, 128, 0.7)',
    };

    const pmValue = parseFloat(value);
    let size = 30;
    if (pmValue <= 30) size = 35;
    else if (pmValue > 30 && pmValue <= 80) size = 40;
    else if (pmValue > 80 && pmValue <= 150) size = 45;
    else if (pmValue > 150) size = 50;

    return {
      width: `${size}px`,
      height: `${size}px`,
      backgroundColor: getColorByPM10(value),
      borderRadius: '5px',
      border: '2px solid #333',
      fontSize: '14px',
      fontWeight: 'bold',
    };
  };


  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  if (!dustData) return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  return (
    <div className="dust-container">
      <h1>Dust-Health-AI</h1>

      <div className="api-data-section">
        <ul className="api-data-list">
          {apiData.map((data) => (
            <li key={data.region} className="api-data-item">
              <h4>  {data.region} - {data.regionDetail} 지역 정보   </h4>
              {data.apiData.length === 0 ? (
                <p>해당 지역의 데이터가 없습니다.</p>
              ) : (
                data.apiData.map((item, index) => (
                  <div key={index} className="measurement-info">
                    <div className="gauge-container">
                      <div className="gauge">
                        <CircularProgressbar
                          value={parseFloat(item.pm10Value) || 0}
                          maxValue={150}
                          text={item.pm10Value ? `${item.pm10Value} ㎍/m³` : 'N/A'}
                          styles={buildStyles({
                            pathColor: getColorByPM10(item.pm10Value),
                            textColor: '#333',
                          })}
                        />
                        <p>미세먼지(PM10)</p>
                      </div>
                      <div className="gauge">
                        <CircularProgressbar
                          value={parseFloat(item.pm25Value) || 0}
                          maxValue={150}
                          text={item.pm25Value ? `${item.pm25Value} ㎍/m³` : 'N/A'}
                          styles={buildStyles({
                            pathColor: getColorByPM25(item.pm25Value),
                            textColor: '#333',
                          })}
                        />
                        <p>초미세먼지(PM2.5)</p>
                       </div>
                      <div className="gauge">
                        <CircularProgressbar
                          value={parseFloat(item.o3Value) || 0}
                          maxValue={1}
                          text={item.o3Value ? `${item.o3Value}ppm` : 'N/A'}
                          styles={buildStyles({
                            pathColor: getColorByPM25(item.o3Value),
                            textColor: '#333',
                          })}
                        />
                        <p>오존(O3)</p>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </li>
          ))}
        </ul>
      </div>
      <div className="dust-legend2">
      <ul>
        <li>
          <span style={{ backgroundColor: 'rgba(76, 80, 175, 0.7)' }}></span> : 좋음
        </li>
        <li>
          <span style={{ backgroundColor: 'rgba(76, 175, 80, 0.7)' }}></span> : 보통
        </li>
        <li>
          <span style={{ backgroundColor: 'rgba(255, 245, 89, 0.7)' }}></span> : 나쁨
        </li>
        <li>
          <span style={{ backgroundColor: 'rgba(244, 67, 54, 0.7)' }}></span> : 매우 나쁨
        </li>
      </ul>
    </div>

      {/* 질병 정보 추가 */}
      {dustData.disease && (
      <div className="disease-box">
        <h3>현재 미세먼지 수치에 따른 {dustData.disease} 대처 요령</h3>
        <div className="disease-content">
          <p>미세먼지별 대처 요령(AI)가 들어갈 부분입니다</p>
        </div>
      </div>
    )}

      <div className="dust-content">
        <p>오늘의 미세먼지 지도</p>
        <MapContainer
          center={[36.5, 128.5]}
          zoom={7}
          scrollWheelZoom={false}
          zoomControl={false}
          className="map-container"
          doubleClickZoom={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            maxZoom={19}
          />

          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[cityMapping[city]];
            if (!coordinates) return null;

            const markerStyle = getMarkerStyleByPM10(value);
            const icon = L.divIcon({
              className: 'custom-icon',
              html: `
                <div class="marker-box" 
                     style="width:${markerStyle.width}; height:${markerStyle.height}; background-color:${markerStyle.backgroundColor}; border-radius:${markerStyle.borderRadius}; border:${markerStyle.border}; display: flex; align-items: center; justify-content: center;">
                  <span class="marker-label" 
                        style="color: rgb(0, 0, 0, 0.9); font-size: ${markerStyle.fontSize}; font-weight: ${markerStyle.fontWeight};">
                    ${cityMapping[city] || city}
                  </span>
                </div>`,
              iconSize: [parseInt(markerStyle.width), parseInt(markerStyle.height)],
            });

            return (
              <Marker key={city} position={coordinates} icon={icon}>
                <Popup>
                  <div style={{ textAlign: 'center', fontFamily: 'Roboto, sans-serif', color: '#333' }}>
                    <strong style={{ fontSize: '16px', color: '#007bff' }}>{cityMapping[city]}</strong> <br />
                    미세먼지(PM10): <span style={{ fontWeight: 'bold' }}>{value}㎍/m³</span> <br />
                    상태: 
                    <span style={{ fontWeight: 'bold', color: getColorByPM10(value) }}>
                      {parseFloat(value) <= 30 ? '좋음' :
                       parseFloat(value) <= 80 ? '보통' :
                       parseFloat(value) <= 150 ? '나쁨' : '매우 나쁨'}
                    </span>
                  </div>
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>

        {/* 미세먼지 수치 기준 */}
        <div className="dust-legend">
          <h4>미세먼지 수치 기준 (PM10)</h4>

          <ul>
            <li>
              <span style={{ backgroundColor: 'rgba(76, 80, 175, 0.7)' }}></span> 0~30: 좋음
            </li>
            <li>
              <span style={{ backgroundColor: 'rgba(76, 175, 80, 0.7)' }}></span> ~80: 보통
            </li>
            <li>
              <span style={{ backgroundColor: 'rgba(255, 245, 89, 0.7)' }}></span> ~150: 나쁨
            </li>
            <li>
              <span style={{ backgroundColor: 'rgba(244, 67, 54, 0.7)' }}></span> 150+: 매우 나쁨
            </li>
          </ul>
        </div>

        <p className="measurement-time">측정일시: {dustData.dataTime}</p>

        <div className="button-container">
          <Link to="/analyze">
            <button className="AI-button">AI 분석 페이지 이동</button>
          </Link>
          <Link to="/main">
            <button className="AI-button">메인화면</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default TodayDust;
