import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';

// 서울을 중심으로 한 기본 좌표 설정
const center = [36.5, 127.5]; // 대한민국 중부 좌표

// 각 도시의 대략적인 좌표 설정 (예시)
const cityCoordinates = {
  서울: [37.5665, 126.978], // 서울
  부산: [35.1796, 129.0756], // 부산
  대구: [35.8714, 128.6014], // 대구
  인천: [37.4563, 126.7052], // 인천
  광주: [35.1595, 126.8526], // 광주
  대전: [36.3504, 127.3845], // 대전
  울산: [35.5384, 129.3114], // 울산
  // 필요한 다른 도시들 추가...
};

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D'
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  // 도시명과 미세먼지 농도를 추출
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  return (
    <div className="dust-container">
      <header className="dust-header">
        <h1>TodayDust 페이지</h1>
      </header>
      <main className="dust-content">
        <p>오늘의 미세먼지</p>
        <MapContainer className="dust-map-container" center={center} zoom={7}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[city];
            if (!coordinates) {
              // 좌표가 없는 경우에는 마커를 렌더링하지 않음
              console.warn(`좌표가 없는 도시: ${city}`);
              return null;
            }
            return (
              <Marker
                key={city}
                position={coordinates}
                icon={L.icon({
                  iconUrl: 'https://cdn.iconscout.com/icon/free/png-256/pm10-2062526-1741781.png',
                  iconSize: [25, 25],
                })}
              >
                <Popup>
                  <strong>{cityMapping[city]}</strong><br />
                  미세먼지 농도: {value} μg/m³
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>

        <p>측정일시: {dustData.dataTime}</p>

        <p>사용자 맞춤 분석</p>
        <Link to="/analyze">
          <button className="AI-button">AI 분석 페이지 이동</button>
        </Link>
      </main>
      <Link to="/main">
        <button className="main-button">Main</button>
      </Link>
    </div>
  );
};


export default TodayDust;
