import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/todaydust.css';


const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=10&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D'
        );
        const data = await response.json();
        setDustData(data.response.body.items);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }
  // 도시명과 미세먼지 농도를 추출
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );
    return (
        <div className="dust-container">
            <header className="dust-header">
                <h1>TodayDust page</h1>
            </header>
            <main className="dust-content">
                <p>오늘의 미세먼지</p>
                <table border="1">
                    <thead>
                    <tr>
                        <th>지역</th>
                        <th>측정일시</th>
                        <th>미세먼지(PM10) 농도(μg/m³)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {dustData.map((item, index) => (
                        <tr key={index}>
                            <td>{item.daegu}</td>
                            <td>{item.dataTime}</td>
                            <td>{item.pm10Value}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>

                <p>사용자 맞춤 분석</p>
                <Link to="/analyze">
                    <button className="AI-button">AI 분석 페이지 이동</button>
                </Link>
            </main>
            <Link to="/main">
                <button className="main-button">Main</button>
            </Link>
        </div>
    );
};

export default TodayDust;
