import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import mapImage from '../assets/korea_map.png';

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=YOUR_API_KEY'
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  // 도시명과 미세먼지 농도를 추출
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  // 미세먼지 농도에 따라 원의 색상을 결정하는 함수
  const getMarkerColor = (value) => {
    if (value > 100) {
      return 'red';
    } else if (value > 50) {
      return 'orange';
    } else {
      return 'green';
    }
  };

  const getMarkerSize = (value) => {
    if (value > 100) {
      return 30;
    } else if (value > 50) {
      return 25;
    } else {
      return 20;
    }
  };

  const renderMarkers = () => {
    return cities.map(([city, value]) => {
      const cityData = cityMapping[city];
      if (cityData) {
        return (
          <div
            key={city}
            className="marker"
            style={{
              position: 'absolute',
              top: `${cityData.y}px`,
              left: `${cityData.x}px`,
              backgroundColor: getMarkerColor(value),
              width: `${getMarkerSize(value)}px`,
              height: `${getMarkerSize(value)}px`,
              borderRadius: '50%',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              color: '#fff',
              fontSize: '12px',
              fontWeight: 'bold',
              textAlign: 'center',
              border: '2px solid #fff',
            }}
            title={`${cityData.name}: ${value} μg/m³`}
          >
            <span>{value}</span>
          </div>
        );
      }
      return null;
    });
  };

  return (
    <div className="dust-container">
      <header className="dust-header">
        <h1>TodayDust 페이지</h1>
      </header>
      <main className="dust-content">
        <p>오늘의 미세먼지</p>
        <div className="map-container" style={{ position: 'relative' }}>
          <img
            src={mapImage}
            alt="Map of Korea"
            style={{ width: '100%', height: 'auto' }}
          />
          {renderMarkers()}
        </div>
        <p>측정일시: {dustData.dataTime}</p>

        <p>사용자 맞춤 분석</p>
        <Link to="/analyze">
          <button className="AI-button">AI 분석 페이지 이동</button>
        </Link>
      </main>
      <Link to="/main">
        <button className="main-button">Main</button>
      </Link>
    </div>
  );
};

export default TodayDust;
