import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, CircleMarker, Tooltip, Popup } from 'react-leaflet';
import L from 'leaflet'; // Leaflet 라이브러리 사용
import 'leaflet/dist/leaflet.css';
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D'
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('데이터 로딩 오류:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>로딩 중...</div>;
  }

  // 도시명과 미세먼지 농도를 추출
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  // 농도에 따라 원의 색상을 다르게 설정하는 함수
  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'gray'; // 데이터가 없을 때 회색
    const pmValue = parseFloat(value);
    if (pmValue <= 30) return 'green'; // 좋음
    if (pmValue <= 80) return 'yellow'; // 보통
    if (pmValue <= 150) return 'orange'; // 나쁨
    return 'red'; // 매우 나쁨
  };

  // 농도에 따라 원의 크기를 조절하는 함수
  const getRadiusByPM10 = (value) => {
    if (!value || value === '-') return 10;
    const pmValue = parseFloat(value);
    if (pmValue <= 30) return 20; // 좋음
    if (pmValue <= 80) return 30; // 보통
    if (pmValue <= 150) return 40; // 나쁨
    return 50; // 매우 나쁨
  };

  // 마커에 그림자 추가 (이미지 마커로 바꾸는 예시)
  const createCustomIcon = (color) => {
    return new L.DivIcon({
      className: 'custom-icon',
      html: `<div style="background-color:${color}; width: 30px; height: 30px; border-radius: 50%; box-shadow: 0 0 10px rgba(0,0,0,0.5);"></div>`,
    });
  };

  return (
    <div className="dust-container">
      <header className="dust-header">
        <h1>오늘의 미세먼지 지도</h1>
      </header>

      <main className="dust-content">
        <p>오늘의 미세먼지 지도</p>

        {/* Leaflet 지도 */}
        <MapContainer
          center={[36.5, 128.0]}
          zoom={7}
          dragging={false}
          scrollWheelZoom={false}
          zoomControl={false}
          className="map-container"
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
            maxZoom={19}
          />

          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[cityMapping[city]];
            if (!coordinates) return null;

            return (
              <CircleMarker
                key={city}
                center={coordinates}
                radius={getRadiusByPM10(value)} // 원 크기
                color={getColorByPM10(value)} // 원 색상
                fillOpacity={0.7}
                icon={createCustomIcon(getColorByPM10(value))} // 커스텀 아이콘 사용
              >
                <Tooltip permanent direction="center" className="custom-tooltip">
                  <span style={{ fontWeight: 'bold', color: 'black', fontSize: '12px' }}>
                    {cityMapping[city]}
                  </span>
                </Tooltip>
                <Popup>
                  <div style={{ textAlign: 'center' }}>
                    <strong>{cityMapping[city]}</strong> <br />
                    미세먼지(PM10): {value}㎍/m³ <br />
                    상태:
                    {parseFloat(value) <= 30 ? '좋음' :
                      parseFloat(value) <= 80 ? '보통' :
                      parseFloat(value) <= 150 ? '나쁨' : '매우 나쁨'}
                  </div>
                </Popup>
              </CircleMarker>
            );
          })}
        </MapContainer>

        <p>측정일시: {dustData.dataTime}</p>

        <div className="button-container">
          <Link to="/analyze">
            <button className="AI-button">AI 분석 페이지 이동</button>
          </Link>
          <Link to="/main">
            <button className="main-button">Main</button>
          </Link>
        </div>
      </main>
    </div>
  );
};

export default TodayDust;
