import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/todaydust.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // Leaflet을 임포트하여 마커 아이콘을 커스터마이징

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [loading, setLoading] = useState(true);

  // 미세먼지 데이터를 가져오는 useEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          'https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D'
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
        setLoading(false);
      } catch (error) {
        console.error('데이터를 가져오는 중 오류가 발생했습니다:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 데이터가 로딩 중일 때 표시할 메시지
  if (loading) {
    return <div>로딩 중...</div>;
  }

  // 도시와 미세먼지 농도 추출
  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  // 각 도시의 정확한 위도와 경도
  const cityCoordinates = {
    서울: { lat: 37.5665, lng: 126.978 },
    부산: { lat: 35.1796, lng: 129.0756 },
    대구: { lat: 35.8666, lng: 128.5964 },
    인천: { lat: 37.4563, lng: 126.7052 },
    광주: { lat: 35.1595, lng: 126.8526 },
    대전: { lat: 36.3504, lng: 127.3845 },
    울산: { lat: 35.5374, lng: 129.3114 }
  };

  return (
    <div className="dust-container">
      <header className="dust-header">
        <h1>오늘의 미세먼지</h1>
      </header>
      <main className="dust-content">
        <p>미세먼지(PM10) 농도</p>

        {/* Leaflet 지도 설정 */}
        <MapContainer center={[36.5, 127.5]} zoom={7} style={{ height: '500px', width: '100%' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
          />
          {/* 도시별 미세먼지 마커 추가 */}
          {cities.map(([city, value]) => {
            const { lat, lng } = cityCoordinates[city] || { lat: 0, lng: 0 };
            return (
              <Marker
                key={city}
                position={[lat, lng]}
                icon={new L.Icon({
                  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
                  iconSize: [25, 41],
                  iconAnchor: [12, 41],
                  popupAnchor: [1, -34],
                })}
              >
                <Popup>
                  <strong>{city}</strong><br />
                  미세먼지 농도: {value} μg/m³
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>

        {/* 미세먼지 측정 일시 */}
        <p>측정일시: {dustData.dataTime}</p>

        <p>사용자 맞춤 분석</p>
        <Link to="/analyze">
          <button className="AI-button">AI 분석 페이지 이동</button>
        </Link>
      </main>

      {/* 메인 페이지로 돌아가는 버튼 */}
      <Link to="/main">
        <button className="main-button">Main</button>
      </Link>
    </div>
  );
};

export default TodayDust;
