import React, { useEffect, useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; 
import axios from 'axios'; 
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js'; 
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState({});
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [loading, setLoading] = useState(true);

  // üîÑ ÌäπÏ†ï ÏßÄÏó≠Ïùò ÎØ∏ÏÑ∏Î®ºÏßÄ Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
  const fetchApiData = async (region) => {
    try {
      const response = await fetch(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D&region=${encodeURIComponent(region)}`
      );
      const data = await response.json();
      return data.response.body.items[0];
    } catch (error) {
      console.error(`Error fetching data for region ${region}:`, error);
      return null;
    }
  };

  // üîÑ DB Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
  const fetchDbData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/products/');
      console.log('DB Îç∞Ïù¥ÌÑ∞:', response.data);
      return response.data;
    } catch (error) {
      console.error('Error fetching DB data:', error);
      return [];
    }
  };

  // üîÑ Î™®Îì† Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§Îäî Î©îÏù∏ Ìï®Ïàò
  const fetchAllData = async () => {
    try {
      const dbDataResult = await fetchDbData();
      const allApiData = await Promise.all(
        dbDataResult.map(async (item) => {
          const region = item.region; 
          const apiDataForRegion = await fetchApiData(region);
          return { region, apiData: apiDataForRegion };
        })
      );
      setDbData(dbDataResult); 
      setApiData(allApiData);
      setLoading(false);
    } catch (error) {
      console.error('Error during data fetching:', error);
      setLoading(false);
    }
  };

  // üü¢ ÏµúÏ¥à Ïã§Ìñâ
  useEffect(() => {
    fetchAllData();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!dustData || Object.keys(dustData).length === 0) {
    return <div>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.</div>;
  }

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 15) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 35) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 75) return 'rgba(255, 245, 89, 0.7)';
    if (pmValue <= 150) return 'rgba(255, 152, 0, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  const getMarkerStyleByPM10 = (value) => {
    if (!value || value === '-') return {
      width: '40px',
      height: '40px',
      backgroundColor: 'rgba(128, 128, 128, 0.7)',
    };

    const pmValue = parseFloat(value);
    let size = 30;
    if (pmValue > 15 && pmValue <= 35) size = 35;
    else if (pmValue > 35 && pmValue <= 75) size = 40;
    else if (pmValue > 75 && pmValue <= 150) size = 45;
    else if (pmValue > 150) size = 50;

    return {
      width: `${size}px`,
      height: `${size}px`,
      backgroundColor: getColorByPM10(value),
      borderRadius: '5px',
      border: '2px solid #333',
      fontSize: '14px',
      fontWeight: 'bold',
    };
  };

  return (
    <div className="dust-container">
      <h2>üåç Ïò§ÎäòÏùò ÎØ∏ÏÑ∏Î®ºÏßÄ Ï†ïÎ≥¥</h2>

      <ul className="api-data-list">
        {apiData.map((data) => (
          <li key={data.region} className="api-data-item">
            <h3>üìç ÏßÄÏó≠: {data.region}</h3>
            {data.apiData ? (
              <>
                <p>üå´Ô∏è ÎØ∏ÏÑ∏Î®ºÏßÄ (PM10): {data.apiData.pm10Value}„éç/m¬≥</p>
                <p>üí® Ïù¥ÏÇ∞ÌôîÏßàÏÜå (NO‚ÇÇ): {data.apiData.no2Value}ppm</p>
                <p>üåç Ïò§Ï°¥ (O‚ÇÉ): {data.apiData.o3Value}ppm</p>
              </>
            ) : (
              <p>Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
            )}
          </li>
        ))}
      </ul>

      <MapContainer center={[36.5, 128.5]} zoom={7} scrollWheelZoom={false} zoomControl={false} className="map-container">
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap" />
        {cities.map(([city, value]) => {
          const coordinates = cityCoordinates[cityMapping[city]];
          if (!coordinates) return null;

          const markerStyle = getMarkerStyleByPM10(value);
          const icon = L.divIcon({
            className: 'custom-icon',
            html: `<div class="marker-box" style="width:${markerStyle.width}; height:${markerStyle.height}; background-color:${markerStyle.backgroundColor};"></div>`,
            iconSize: [parseInt(markerStyle.width, 10), parseInt(markerStyle.height, 10)]
          });

          return (
            <Marker key={city} position={coordinates} icon={icon}>
              <Popup>
                <strong>{cityMapping[city]}</strong> <br />
                ÎØ∏ÏÑ∏Î®ºÏßÄ(PM10): {value}„éç/m¬≥ <br />
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>

      <div className="dust-legend">
        <h4>ÎØ∏ÏÑ∏Î®ºÏßÄ ÏàòÏπò Í∏∞Ï§Ä (PM10)</h4>
        <ul>
          <li><span style={{ backgroundColor: 'rgba(76, 80, 175, 0.7)' }}></span> 0~15: Îß§Ïö∞ Ï¢ãÏùå</li>
          <li><span style={{ backgroundColor: 'rgba(76, 175, 80, 0.7)' }}></span> 16~35: Ï¢ãÏùå</li>
          <li><span style={{ backgroundColor: 'rgba(255, 245, 89, 0.7)' }}></span> 36~75: Î≥¥ÌÜµ</li>
          <li><span style={{ backgroundColor: 'rgba(255, 152, 0, 0.7)' }}></span> 76~150: ÎÇòÏÅ®</li>
          <li><span style={{ backgroundColor: 'rgba(244, 67, 54, 0.7)' }}></span> 150+: Îß§Ïö∞ ÎÇòÏÅ®</li>
        </ul>
      </div>

      <p>Ï∏°Ï†ïÏùºÏãú: {dustData.dataTime}</p>

      <Link to="/analyze"><button className="AI-button">AI Î∂ÑÏÑù ÌéòÏù¥ÏßÄ Ïù¥Îèô</button></Link>
      <Link to="/main"><button className="AI-button">Î©îÏù∏ÌôîÎ©¥</button></Link>
    </div>
  );
};

export default TodayDust;
