import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import { ArrowRight, Map, AlertTriangle, Wind, Loader2 } from 'lucide-react';
import axios from 'axios';
import L from 'leaflet';
import 'react-circular-progressbar/dist/styles.css';
import 'leaflet/dist/leaflet.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState(null);
  const [dbData, setDbData] = useState([]);
  const [apiData, setApiData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = 'pVHLUl3UGVDmm%2BB0aUxMttCQnoCePxtWLb4WJKushUW6XjTX93l89IkU%2FdELSR6nZ318fR3efuSBb3gVpjAFFA%3D%3D';

  // 미세먼지 수준에 따른 색상 및 메시지
  const getDustLevelInfo = (value) => {
    if (!value || value === '-') return { color: 'gray-400', message: '정보 없음' };
    const pmValue = parseFloat(value);
    if (pmValue <= 30) return { color: 'blue-600', message: '좋음' };
    if (pmValue <= 80) return { color: 'green-500', message: '보통' };
    if (pmValue <= 150) return { color: 'yellow-400', message: '나쁨' };
    return { color: 'red-500', message: '매우 나쁨' };
  };

  // API 호출 함수들
  const fetchDustData = async () => {
    try {
      const response = await fetch(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=${API_KEY}`
      );
      const data = await response.json();
      return data.response.body.items[0];
    } catch (error) {
      console.error('Error fetching dust data:', error);
      throw error;
    }
  };

  const fetchDbData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/products/');
      return response.data;
    } catch (error) {
      console.error('Error fetching DB data:', error);
      throw error;
    }
  };

  const fetchDetailData = async (region, regionDetail) => {
    try {
      const response = await axios.get(
        `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureSidoLIst?sidoName=${region}&searchCondition=DAILY&pageNo=1&numOfRows=100&returnType=json&serviceKey=${API_KEY}`
      );

      const allItems = response.data.response.body.items;
      const matchingItems = allItems.filter(item => item.cityName === regionDetail);

      return matchingItems.length > 0 ? [matchingItems[0]] : [];
    } catch (error) {
      console.error(`Error fetching detail data for ${region}:`, error);
      return [];
    }
  };

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);
        const [dustResult, dbResult] = await Promise.all([
          fetchDustData(),
          fetchDbData()
        ]);

        setDustData(dustResult);
        setDbData(dbResult);

        if (dbResult.length > 0) {
          const { region, region_detail } = dbResult[0];
          const detailData = await fetchDetailData(region, region_detail);

          setApiData([{ 
            region,
            regionDetail: region_detail,
            apiData: detailData 
          }]);

          // 질병 정보 추가
          const diseaseInfo = dbResult.find(item => item.region === region);
          if (diseaseInfo) {
            setDustData(prev => ({
              ...prev,
              disease: diseaseInfo.disease,
              diseaseAdvice: diseaseInfo.diseaseAdvice,
            }));
          }
        }
      } catch (error) {
        setError(error.message);
        console.error('Error in fetchAllData:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  const getMarkerIcon = (value, city) => {
    const { color, message } = getDustLevelInfo(value);
    const size = parseFloat(value) > 150 ? 50 : 40;

    return L.divIcon({
      className: 'custom-marker-icon',
      html: `
        <div class="bg-${color} w-${size} h-${size} flex items-center justify-center rounded shadow-lg">
          <span class="text-white font-semibold text-sm">${cityMapping[city] || city}</span>
        </div>
      `,
      iconSize: [size, size],
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="flex flex-col items-center gap-4">
          <Loader2 className="w-12 h-12 text-blue-600 animate-spin" />
          <p className="text-gray-600 font-medium">데이터를 불러오는 중...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="bg-red-50 p-6 rounded-lg">
          <p className="text-red-600">오류가 발생했습니다: {error}</p>
          <button 
            onClick={() => window.location.reload()}
            className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors"
          >
            다시 시도
          </button>
        </div>
      </div>
    );
  }

  if (!dustData) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-gray-600">데이터를 불러올 수 없습니다. 다시 시도해주세요.</p>
      </div>
    );
  }

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-blue-900 mb-2">Dust-Health-AI</h1>
          <p className="text-gray-600">실시간 대기질 모니터링 대시보드</p>
        </header>

        {/* API 데이터 섹션 */}
        <div className="mb-12">
          <div className="bg-white rounded-2xl shadow-lg p-6 mb-8">
            {apiData.map((data) => (
              <div key={data.region} className="space-y-6">
                <h4 className="text-xl font-semibold text-blue-800 flex items-center justify-center gap-2">
                  <Map className="w-5 h-5" />
                  {data.region} - {data.regionDetail}
                </h4>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                  {data.apiData.map((item, index) => (
                    <div key={index} className="bg-blue-50 rounded-xl p-6 transition-transform hover:scale-105">
                      <CircularProgressbar
                        value={parseFloat(item.pm10Value) || 0}
                        maxValue={150}
                        text={item.pm10Value ? `${item.pm10Value}㎍/m³` : 'N/A'}
                        styles={buildStyles({
                          pathColor: getDustLevelInfo(item.pm10Value).color,
                          textColor: '#1e40af',
                          trailColor: '#e2e8f0'
                        })}
                      />
                      <p className="text-center mt-4 font-medium text-gray-700">미세먼지(PM10)</p>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 범례 */}
        <div className="bg-white rounded-xl shadow-md p-4 mb-8">
          <div className="flex justify-center items-center gap-6 flex-wrap">
            {[
              { color: 'bg-blue-600', label: '좋음' },
              { color: 'bg-green-500', label: '보통' },
              { color: 'bg-yellow-400', label: '나쁨' },
              { color: 'bg-red-500', label: '매우 나쁨' }
            ].map(({ color, label }) => (
              <div key={label} className="flex items-center gap-2">
                <div className={`w-4 h-4 ${color} rounded`}></div>
                <span className="text-sm text-gray-600">{label}</span>
              </div>
            ))}
          </div>
        </div>

        {/* 질병 정보 박스 */}
        {dustData?.disease && (
          <div className="bg-white rounded-2xl shadow-lg p-8 mb-12">
            <h3 className="text-xl font-semibold text-blue-800 mb-6 flex items-center gap-2">
              <AlertTriangle className="w-6 h-6 text-yellow-500" />
              현재 미세먼지 수치에 따른 {dustData.disease} 대처 요령
            </h3>
            <div className="bg-blue-50 rounded-xl p-6">
              <p className="text-gray-700">{dustData.diseaseAdvice || '미세먼지별 대처 요령(AI)가 들어갈 부분입니다'}</p>
            </div>
          </div>
        )}

        {/* 지도 섹션 */}
        <div className="bg-white rounded-2xl shadow-lg p-6 mb-8">
          <h2 className="text-xl font-semibold text-blue-800 mb-6 flex items-center justify-center gap-2">
            <Wind className="w-6 h-6" />
            오늘의 미세먼지 지도
          </h2>
          <div className="h-[600px] rounded-xl overflow-hidden">
            <MapContainer
              center={[36.5, 128.5]}
              zoom={7}
              className="h-full w-full"
              zoomControl={false}
              scrollWheelZoom={false}
            >
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              {cities.map(([city, value]) => {
                const coordinates = cityCoordinates[cityMapping[city]];
                if (!coordinates) return null;

                return (
                  <Marker
                    key={city}
                    position={coordinates}
                    icon={getMarkerIcon(value)}
                  >
                    <Popup className="custom-popup">
                      <div className="text-center">
                        <h3 className="font-semibold text-blue-800">{cityMapping[city]}</h3>
                        <p className="text-gray-600">미세먼지(PM10): {value}㎍/m³</p>
                        <p className={`text-${getDustLevelInfo(value).color}`}>
                          {getDustLevelInfo(value).message}
                        </p>
                      </div>
                    </Popup>
                  </Marker>
                );
              })}
            </MapContainer>
          </div>
          <p className="text-center text-gray-500 mt-4">측정일시: {dustData.dataTime}</p>
        </div>

        {/* 버튼 그룹 */}
        <div className="flex justify-center gap-4">
          <Link to="/analyze">
            <button className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg transition-colors">
              AI 분석 페이지 <ArrowRight className="w-5 h-5" />
            </button>
          </Link>
          <Link to="/main">
            <button className="flex items-center gap-2 bg-gray-600 hover:bg-gray-700 text-white px-6 py-3 rounded-lg transition-colors">
              메인화면 <ArrowRight className="w-5 h-5" />
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default TodayDust;
