import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // axios 추가
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; 
import '../css/todaydust.css';
import cityMapping from '../region_mapping/cityMapping.js';
import cityCoordinates from '../region_mapping/cityPosition.js';

const TodayDust = () => {
  const [dustData, setDustData] = useState([]);
  const [dbData, setDbData] = useState([]); // DB 데이터 추가
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDustData = async () => {
      try {
        const response = await fetch(
          `https://apis.data.go.kr/B552584/ArpltnStatsSvc/getCtprvnMesureLIst?itemCode=PM10&dataGubun=HOUR&pageNo=1&numOfRows=1&returnType=json&serviceKey=zhvs5TlKznNkfpG91l4BPgIcZtbsxovufWhyA4%2Bw2KcaA1dp6RsGVOYHyD91i%2FXzDfAqOFIdScVjvbElsw%2BBCQ%3D%3D`
        );
        const data = await response.json();
        setDustData(data.response.body.items[0]);
      } catch (error) {
        console.error('Error fetching dust data:', error);
      }
    };

    const fetchDbData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/products/'); // Django API 호출
        console.log('DB 데이터:', response.data); // DB 데이터 확인을 위한 콘솔 출력
        setDbData(response.data);
      } catch (error) {
        console.error('Error fetching DB data:', error);
      }
    };

    fetchDustData();
    fetchDbData(); // DB 데이터 가져오기
    setLoading(false);
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (!dustData || dustData.length === 0) {
    return <div>데이터를 불러올 수 없습니다. 다시 시도해주세요.</div>;
  }

  const cities = Object.entries(dustData).filter(
    ([key]) => key !== 'dataTime' && key !== 'dataGubun' && key !== 'itemCode'
  );

  const getColorByPM10 = (value) => {
    if (!value || value === '-') return 'rgba(128, 128, 128, 0.7)';
    const pmValue = parseFloat(value);
    if (pmValue <= 15) return 'rgba(76, 80, 175, 0.7)';
    if (pmValue <= 35) return 'rgba(76, 175, 80, 0.7)';
    if (pmValue <= 75) return 'rgba(255, 245, 89, 0.7)';
    if (pmValue <= 150) return 'rgba(255, 152, 0, 0.7)';
    return 'rgba(244, 67, 54, 0.7)';
  };

  const getMarkerStyleByPM10 = (value) => {
    if (!value || value === '-') return {
      width: '40px',
      height: '40px',
      backgroundColor: 'rgba(128, 128, 128, 0.7)',
    };

    const pmValue = parseFloat(value);
    let size = 30;
    if (pmValue > 15 && pmValue <= 35) size = 35;
    else if (pmValue > 35 && pmValue <= 75) size = 40;
    else if (pmValue > 75 && pmValue <= 150) size = 45;
    else if (pmValue > 150) size = 50;

    return {
      width: `${size}px`,
      height: `${size}px`,
      backgroundColor: getColorByPM10(value),
      borderRadius: '5px',
      border: '2px solid #333',
      fontSize: '14px',
      fontWeight: 'bold',
    };
  };

  return (
    <div className="dust-container">
      <main className="dust-content">
        <p>오늘의 미세먼지 지도</p>

        <MapContainer
          center={[36.5, 128.5]}
          zoom={7}
          scrollWheelZoom={false}
          zoomControl={false}
          className="map-container"
          doubleClickZoom={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            maxZoom={19}
          />

          {cities.map(([city, value]) => {
            const coordinates = cityCoordinates[cityMapping[city]];
            if (!coordinates) return null;

            const markerStyle = getMarkerStyleByPM10(value);
            const icon = L.divIcon({
              className: 'custom-icon',
              html: `<div class="marker-box" style="width:${markerStyle.width}; height:${markerStyle.height}; background-color:${markerStyle.backgroundColor};"></div>`,
              iconSize: [parseInt(markerStyle.width), parseInt(markerStyle.height)]
            });

            return (
              <Marker key={city} position={coordinates} icon={icon}>
                <Popup>
                  <strong>{cityMapping[city]}</strong> <br />
                  미세먼지(PM10): {value}㎍/m³
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>

        <h2>📊 **DB에서 불러온 데이터**</h2>
        <ul className="db-data-list">
          {dbData.map((item) => (
            <li key={item.id}>
              📍 지역: {item.region} | 🦠 질병: {item.disease}
            </li>
          ))}
        </ul>

        <p>측정일시: {dustData.dataTime}</p>

        <div className="button-container">
          <Link to="/analyze">
            <button className="AI-button">AI 분석 페이지 이동</button>
          </Link>
          <Link to="/main">
            <button className="AI-button">메인화면</button>
          </Link>
        </div>
      </main>
    </div>
  );
};

export default TodayDust;
